import React, {useState } from "react";

function Login() {
  const [fullname, setFullname] = useState('');
  const [age, setAge] = useState('');
  const [location, setLocation] = useState('');
  const [module, setModule] = useState();

  return(
    <form className="Form">
        <input type="text" placeholder="Nome completo"
          value={fullname}
          onChange={({ target }) => setFullname(target.value)}
        />
        <input type="number" placeholder="Idade" />
        <input type="text" placeholder="Cidade" />
        <label htmlFor="Fundamentos">
        <input type="radio" name="Fundamentos"/>
        Fundamentos
        </label>
        <label htmlFor="Front-end">
        <input type="radio" name="Front-end"/>
        Front-end
        </label>
        <label htmlFor="Back-end">
        <input type="radio" name="Back-end"/>
        Back-end
        </label>
        <label htmlFor="Ciência da Computação">
        <input type="radio" name="Ciência da Computação"/>
        Ciência da Computação
        </label>
        <input type="button" name="button" value="Enviar"/>
    </form>
  );
}

export default Login;
